import axios from "axios";

import { domain } from "../../constants/defaultValues";
import { validateStatusConfig } from "./Axios";

export const login = userInfo => {
  return axios.post(`${domain}/login`, userInfo, validateStatusConfig);
};

export const register = userInfo => {
  return axios.post(`${domain}/signup`, userInfo, validateStatusConfig);
};

export const registerWithInvitation = (userInfo, inviteId) => {
  return axios.post(
    `${domain}/signup/${inviteId}`,
    userInfo,
    validateStatusConfig
  );
};

export const getInvitation = inviteId => {
  return axios.get(`${domain}/signup/${inviteId}`, validateStatusConfig);
};

export const requestResetPassword = formProps => {
  return axios.post(`${domain}/forgot-password`, formProps);
};

export const resetPassword = (formProps, userId, tempPassword) => {
  return axios.post(
    `${domain}/forgot-password/${userId}/${tempPassword}`,
    formProps
  );
};
