import React, { Component, Fragment } from "react";
import { Row } from "reactstrap";
import { connect } from "react-redux";

import DataListView from "./DataListView";
import { NotificationManager } from "../../../../components/common/react-notifications";
import Pagination from "../../../../components/Pagination";
import ListPageHeading from "../../../../components/ListPageHeading";
import AddNewModal from "./AddNewModal";
import {
  getExpenses,
  deleteExpenses
} from "../../../../helpers/Server/Category";
import {
  getExpensesAsync,
  deleteCExpensesAsync
} from "../../../../redux/actions";

class BrowseAll extends Component {
  constructor(props) {
    super(props);
    this.mouseTrap = require("mousetrap");

    this.state = {
      pageSize: 5,
      orderOptions: [
        { column: "title", label: "Expense Name" },
        { column: "date", label: "Date Created" }
      ],

      selectedOrderOption: { column: "title", label: "Expense Name" },
      dropdownSplitOpen: false,
      modalOpen: false,
      currentPage: 1,
      totalPage: 1,
      search: "",
      selectedExpenses: [],
      lastChecked: null,
      isLoading: false
    };
  }

  componentDidMount() {
    this.dataListRender();
    this.mouseTrap.bind(["ctrl+a", "command+a"], () =>
      this.handleChangeSelectAll(false)
    );
    this.mouseTrap.bind(["ctrl+d", "command+d"], () => {
      this.setState({
        selectedExpenses: []
      });
      return false;
    });
  }

  componentWillUnmount() {
    this.mouseTrap.unbind("ctrl+a");
    this.mouseTrap.unbind("command+a");
    this.mouseTrap.unbind("ctrl+d");
    this.mouseTrap.unbind("command+d");
  }

  toggleModal = () => {
    this.setState({
      modalOpen: !this.state.modalOpen
    });
  };

  changeOrderBy = column => {
    this.setState(
      {
        selectedOrderOption: this.state.orderOptions.find(
          x => x.column === column
        )
      },
      () => this.dataListRender()
    );
  };

  onChangePage = page => {
    this.setState(
      {
        currentPage: page
      },
      () => this.dataListRender()
    );
  };

  onSearchKey = e => {
    if (e.key === "Enter") {
      this.setState(
        {
          search: e.target.value.toLowerCase()
        },
        () => this.dataListRender()
      );
    }
  };

  onCheckExpense = (event, id) => {
    if (
      event.target.tagName === "A" ||
      (event.target.parentElement && event.target.parentElement.tagName === "A")
    ) {
      return true;
    }
    if (this.state.lastChecked === null) {
      this.setState({
        lastChecked: id
      });
    }

    let selectedExpenses = this.state.selectedExpenses;
    if (selectedExpenses.includes(id)) {
      selectedExpenses = selectedExpenses.filter(x => x !== id);
    } else {
      selectedExpenses.push(id);
    }
    this.setState({
      selectedExpenses
    });

    if (event.shiftKey) {
      var expenses = this.props.expenses;
      var start = this.getIndex(id, expenses, "id");
      var end = this.getIndex(this.state.lastChecked, expenses, "id");
      expenses = expenses.slice(Math.min(start, end), Math.max(start, end) + 1);
      selectedExpenses.push(
        ...expenses.map(expense => {
          return expense._id;
        })
      );
      selectedExpenses = Array.from(new Set(selectedExpenses));
      this.setState({
        selectedExpenses
      });
    }
    document.activeElement.blur();
  };

  getIndex(value, arr, prop) {
    for (var i = 0; i < arr.length; i++) {
      if (arr[i][prop] === value) {
        return i;
      }
    }
    return -1;
  }

  updateExpenses = () => {
    const skipEntries = (this.state.currentPage - 1) * this.state.pageSize;
    const serverCall = () =>
      getExpenses({ skipEntries, pageSize: this.state.pageSize });
    this.setState({ isLoading: true });
    this.props.getCategoriesAsync(
      serverCall,
      () => {
        this.setState({
          selectedExpenses: [],
          isLoading: false,
          totalPage: Math.ceil(
            this.props.totalExpensesCount / this.state.pageSize
          )
        });
      },
      (succeed, message) => {
        this.setState({ isLoading: false });
      }
    );
  };

  handleChangeSelectAll = isToggle => {
    if (this.state.selectedExpenses.length >= this.props.totalExpensesCount) {
      if (isToggle) {
        this.setState({
          selectedExpenses: []
        });
      }
    } else {
      this.setState({
        selectedExpenses: this.props.expenses.map(x => x._id)
      });
    }
    document.activeElement.blur();
    return false;
  };

  dataListRender() {
    this.updateCategories();
  }

  handleDelete = () => {
    if (!this.isLoading && this.selectedExpensesLength != 0) {
      const serverCall = () =>
        deleteExpenses({ expenseIDs: this.state.selectedExpenses });
      this.setState({ isLoading: true });
      this.props.deleteExpensesAsync(
        serverCall,
        () => {
          this.setState({ isLoading: false });
          this.updateExpenses();
        },
        (succeed, message) => {
          this.setState({ isLoading: false });
          if (succeed) {
            NotificationManager.success(
              message,
              "Success",
              3000,
              null,
              null,
              ""
            );
          } else {
            NotificationManager.error(
              message,
              "Create Expense Fail",
              3000,
              null,
              null,
              ""
            );
          }
        }
      );
    }
  };

  render() {
    const {
      currentPage,
      selectedOrderOption,
      selectedExpenses,
      orderOptions,
      pageSize
    } = this.state;
    const { match } = this.props;
    const startIndex = (currentPage - 1) * this.state.pageSize + 1;
    const endIndex = Math.min(
      this.props.totalExpensesCount,
      startIndex + pageSize - 1
    );

    return this.state.isLoading ? (
      <div className="loading" />
    ) : (
      <Fragment>
        <div className="disable-text-selection">
          <ListPageHeading
            heading="menu.expenses"
            handleDelete={this.handleDelete}
            handleChangeSelectAll={this.handleChangeSelectAll}
            changeOrderBy={this.changeOrderBy}
            changePageSize={this.changePageSize}
            pageSize={this.state.pageSize}
            totalItemsCount={this.props.totalExpensesCount}
            selectedOrderOption={selectedOrderOption}
            match={match}
            startIndex={startIndex}
            endIndex={endIndex}
            selectedItemsLength={selectedExpenses ? selectedExpenses.length : 0}
            onSearchKey={this.onSearchKey}
            orderOptions={orderOptions}
            toggleModal={this.toggleModal}
          />
          <AddNewModal
            modalOpen={this.state.modalOpen}
            toggleModal={this.toggleModal}
            updateExpenses={this.updateExpenses}
            expenses={this.props.expenses}
          />
          <Row>
            {this.props.expenses.map(expense => {
              return (
                <DataListView
                  key={expense._id}
                  expense={expense}
                  isSelect={this.state.selectedExpenses.includes(expense._id)}
                  onCheckExpense={this.onCheckExpense}
                />
              );
            })}{" "}
            <Pagination
              currentPage={this.state.currentPage}
              totalPage={this.state.totalPage}
              onChangePage={i => this.onChangePage(i)}
            />
          </Row>
        </div>
      </Fragment>
    );
  }

  static getDerivedStateFromProps(nextProps, prevState) {
    return {
      totalPage: Math.ceil(nextProps.totalExpensesCount / prevState.pageSize)
    };
  }
}

const mapStateToProps = state => {
  return {
    expenses: state.expense.expenses,
    totalExpensesCount: state.expense.totalExpensesCount,
    error: state.expense.error
  };
};

export default connect(mapStateToProps, {
  getExpensesAsync,
  deleteExpensesAsync
})(BrowseAll);
