import axios from "axios";

import { domain } from "../../constants/defaultValues";
import { validateStatusConfig } from "./Axios";

export const createCategory = categoryInfo => {
  return axios.post(`${domain}/categories`, categoryInfo, validateStatusConfig);
};

export const getCategories = params => {
  return axios.get(`${domain}/categories`, { params }, validateStatusConfig);
};

export const deleteCategories = data => {
  return axios.delete(`${domain}/categories`, { data }, validateStatusConfig);
};
