import axios from "axios";

import { domain } from "../../constants/defaultValues";
import { validateStatusConfig } from "./Axios";

export const addExpense = expenseInfo => {
  return axios.post(`${domain}/expenses`, expenseInfo, validateStatusConfig);
};

export const getCategories = data => {
  return axios.get(
    `${domain}/expenses`,
    { params: data },
    validateStatusConfig
  );
};

export const deleteCategories = expenseIDs => {
  return axios.delete(
    `${domain}/expenses`,
    { data: expenseIDs },
    validateStatusConfig
  );
};
