import axios from "axios";

import { domain } from "../../constants/defaultValues";
import { validateStatusConfig } from "./Axios";

export const createCategory = categoryInfo => {
  return axios.post(`${domain}/categories`, categoryInfo, validateStatusConfig);
};

export const getCategories = data => {
  return axios.get(
    `${domain}/categories`,
    { params: data },
    validateStatusConfig
  );
};

export const deleteCategories = categoryIDs => {
  return axios.delete(
    `${domain}/categories`,
    { data: categoryIDs },
    validateStatusConfig
  );
};
