import axios from "axios";

import { domain } from "../../constants/defaultValues";
import { validateStatusConfig } from "./Axios";

export const addExpenditure = expenditureInfo => {
  return axios.post(`${domain}/login`, expenditureInfo, validateStatusConfig);
};
