import axios from "axios";

import { domain } from "../../constants/defaultValues";
import { validateStatusConfig } from "./Axios";

export const createExpense = expenseInfo => {
  return axios.post(`${domain}/expenses`, expenseInfo, validateStatusConfig);
};

export const getExpenses = data => {
  return axios.get(
    `${domain}/expenses`,
    { params: data },
    validateStatusConfig
  );
};

export const deleteExpenses = expenseIDs => {
  return axios.delete(
    `${domain}/expenses`,
    { data: expenseIDs },
    validateStatusConfig
  );
};
