import React, { Component, Fragment } from "react";
import { Row } from "reactstrap";
import { connect } from "react-redux";

import DataListView from "./DataListView";
import Pagination from "./Pagination";
import ListPageHeading from "./ListPageHeading";
import AddNewModal from "./AddNewModal";
import {
  getCategories,
  deleteCategories
} from "../../../../helpers/Server/Category";
import {
  getCategoriesAsync,
  deleteCategoriesAsync
} from "../../../../redux/actions";
import ContextMenuContainer from "./ContextMenuContainer";

class BrowseAll extends Component {
  constructor(props) {
    super(props);
    this.mouseTrap = require("mousetrap");

    this.state = {
      pageSize: 5,
      orderOptions: [
        { column: "title", label: "Category Name" },
        { column: "date", label: "Date Created" },
        { column: "size", label: "Size" }
      ],

      selectedOrderOption: { column: "title", label: "Category Name" },
      dropdownSplitOpen: false,
      modalOpen: false,
      currentPage: 1,
      totalPage: 1,
      search: "",
      selectedCategories: [],
      lastChecked: null,
      isLoading: false
    };
  }

  componentDidMount() {
    this.dataListRender();
    this.mouseTrap.bind(["ctrl+a", "command+a"], () =>
      this.handleChangeSelectAll(false)
    );
    this.mouseTrap.bind(["ctrl+d", "command+d"], () => {
      this.setState({
        selectedCategories: []
      });
      return false;
    });
  }

  componentWillUnmount() {
    this.mouseTrap.unbind("ctrl+a");
    this.mouseTrap.unbind("command+a");
    this.mouseTrap.unbind("ctrl+d");
    this.mouseTrap.unbind("command+d");
  }

  toggleModal = () => {
    this.setState({
      modalOpen: !this.state.modalOpen
    });
  };

  changeOrderBy = column => {
    this.setState(
      {
        selectedOrderOption: this.state.orderOptions.find(
          x => x.column === column
        )
      },
      () => this.dataListRender()
    );
  };

  onChangePage = page => {
    this.setState(
      {
        currentPage: page
      },
      () => this.dataListRender()
    );
  };

  onSearchKey = e => {
    if (e.key === "Enter") {
      this.setState(
        {
          search: e.target.value.toLowerCase()
        },
        () => this.dataListRender()
      );
    }
  };

  onCheckCategory = (event, id) => {
    if (
      event.target.tagName === "A" ||
      (event.target.parentElement && event.target.parentElement.tagName === "A")
    ) {
      return true;
    }
    if (this.state.lastChecked === null) {
      this.setState({
        lastChecked: id
      });
    }

    let selectedCategories = this.state.selectedCategories;
    if (selectedCategories.includes(id)) {
      selectedCategories = selectedCategories.filter(x => x !== id);
    } else {
      selectedCategories.push(id);
    }
    this.setState({
      selectedCategories
    });

    if (event.shiftKey) {
      var categories = this.props.categories;
      var start = this.getIndex(id, categories, "id");
      var end = this.getIndex(this.state.lastChecked, categories, "id");
      categories = categories.slice(
        Math.min(start, end),
        Math.max(start, end) + 1
      );
      selectedCategories.push(
        ...categories.map(category => {
          return category._id;
        })
      );
      selectedCategories = Array.from(new Set(selectedCategories));
      this.setState({
        selectedCategories
      });
    }
    document.activeElement.blur();

    console.log("selectedCategories", this.state.selectedCategories);
  };

  getIndex(value, arr, prop) {
    for (var i = 0; i < arr.length; i++) {
      if (arr[i][prop] === value) {
        return i;
      }
    }
    return -1;
  }

  handleChangeSelectAll = isToggle => {
    if (
      this.state.selectedCategories.length >= this.props.totalCategoriesCount
    ) {
      if (isToggle) {
        this.setState({
          selectedCategories: []
        });
      }
    } else {
      this.setState({
        selectedCategories: this.props.categories.map(x => x._id)
      });
    }
    document.activeElement.blur();
    return false;
  };

  dataListRender() {
    const skipEntries = (this.state.currentPage - 1) * this.state.pageSize;
    const serverCall = () =>
      getCategories({ skipEntries, pageSize: this.state.pageSize });
    this.setState({ isLoading: true });
    this.props.getCategoriesAsync(
      serverCall,
      () => {
        this.setState({
          selectedCategories: [],
          isLoading: false,
          totalPage: Math.ceil(
            this.props.totalCategoriesCount / this.state.pageSize
          )
        });
      },
      (succeed, message) => {
        this.setState({ isLoading: false });
      }
    );
  }

  onContextMenuClick = (e, data, target) => {
    console.log(
      "onContextMenuClick - selected categories",
      this.state.selectedCategories
    );
    console.log("onContextMenuClick - action : ", data.action);
  };

  onContextMenu = (e, data) => {
    const clickedCategoryId = data.data;
    if (!this.state.selectedCategories.includes(clickedCategoryId)) {
      this.setState({
        selectedCategories: [clickedCategoryId]
      });
    }
    return true;
  };

  handleDelete = () => {
    const serverCall = () =>
      deleteCategories({ categoryIDs: this.state.selectedCategories });
    this.setState({ isLoading: true });
    this.props.deleteCategoriesAsync(
      serverCall,
      () => {
        this.setState({ isLoading: false });
        console.log("call back");
      },
      (succeed, message) => {
        this.setState({ isLoading: false });
      }
    );
  };

  render() {
    const {
      currentPage,
      selectedOrderOption,
      selectedCategories,
      orderOptions,
      pageSize
    } = this.state;
    const { match } = this.props;
    const startIndex = (currentPage - 1) * this.state.pageSize + 1;
    const endIndex = Math.min(
      this.props.totalCategoriesCount,
      startIndex + pageSize - 1
    );

    return this.state.isLoading ? (
      <div className="loading" />
    ) : (
      <Fragment>
        <div className="disable-text-selection">
          <ListPageHeading
            heading="menu.categories"
            handleDelete={this.handleDelete}
            handleChangeSelectAll={this.handleChangeSelectAll}
            changeOrderBy={this.changeOrderBy}
            changePageSize={this.changePageSize}
            pageSize={this.state.pageSize}
            totalCategoriesCount={this.props.totalCategoriesCount}
            selectedOrderOption={selectedOrderOption}
            match={match}
            startIndex={startIndex}
            endIndex={endIndex}
            selectedCategoriesLength={
              selectedCategories ? selectedCategories.length : 0
            }
            onSearchKey={this.onSearchKey}
            orderOptions={orderOptions}
            toggleModal={this.toggleModal}
          />
          <AddNewModal
            modalOpen={this.state.modalOpen}
            toggleModal={this.toggleModal}
            categories={this.props.categories}
          />
          <Row>
            {this.props.categories.map(category => {
              return (
                <DataListView
                  key={category._id}
                  category={category}
                  isSelect={this.state.selectedCategories.includes(
                    category._id
                  )}
                  onCheckCategory={this.onCheckCategory}
                />
              );
            })}{" "}
            <Pagination
              currentPage={this.state.currentPage}
              totalPage={this.state.totalPage}
              onChangePage={i => this.onChangePage(i)}
            />
            <ContextMenuContainer
              onContextMenuClick={this.onContextMenuClick}
              onContextMenu={this.onContextMenu}
            />
          </Row>
        </div>
      </Fragment>
    );
  }

  static getDerivedStateFromProps(nextProps, prevState) {
    return {
      totalPage: Math.ceil(nextProps.totalCategoriesCount / prevState.pageSize)
    };
  }
}

const mapStateToProps = state => {
  return {
    categories: state.category.categories,
    totalCategoriesCount: state.category.totalCategoriesCount,
    error: state.category.error
  };
};

export default connect(mapStateToProps, {
  getCategoriesAsync,
  deleteCategoriesAsync
})(BrowseAll);
